import React, { useEffect, useState } from "react";
import {MenuItem, FormControl, Select} from "@material-ui/core"
import InfoBox from "./InfoBox";
import Map from "./Map"
import './App.css';

function App() {
  const [countries, setCountries] = useState([]);
  const [country, setCounty] = useState('worldwide');

  useEffect(() => {
    const getCountriesData = async () => {
      await fetch ("https://disease.sh/v3/covid-19/countries")
      .then((response) => response.json())
      .then((data) => {
        const countries = data.map((country) => ({
          name: country.country,
          value: country.countryInfo.iso2
        }));

        setCountries(countries);
      })
    }
    getCountriesData();
  }, []);

  const onCountryChange = (event) => {
    const countryCode = event.target.value;
    setCounty(countryCode);
  }

  return (
    <div className="app">
      <div className="app_left">
        <div className="app_header">
          {/*Header */}
          {/*Title + Select input dropdown field */}
          <h1>COVID-19 TRACKER</h1>
          <FormControl className="app_dropdown">
            <Select varient="outlined" 
              onChange={onCountryChange}
          value={country}>
            {/*Loop through all the countries and show a drop down list*/}
            <MenuItem value="worldwide">Worldwide</MenuItem>
            {countries.map((country) => (
              <MenuItem value={country.value}>{country.name}</MenuItem>
            ))}
          </Select>
        </FormControl>
      </div>

      <div className="app_stats">
        {/*InfoBoxs title="Coronavirus cases"*/}
        <InfoBox title="Coronavirus Cases" cases={123} total={2000}/>
        {/*InfoBoxs title="Coronavirus recoveries"*/}
        <InfoBox title="Recovered" cases={345} total={3000}/>
        {/*InfoBoxs title="Coronavirus deaths"*/}
        <InfoBox title="Deaths" cases={567} total={4000}/>

      </div>






      {/*Table */}
      {/*Graph */}

      {/*Map */}
      <Map />
      </div>

    </div>
  );
}

export default App;
